  clean_names(c("c", "Payment Method", "Orders", "(%) by Paymant Method")) |>
  gt() |>
  fmt_integer(columns = Orders) |>
  fmt_number(columns = `(%) by Paymant Method`) |>
  tab_options(column_labels.font.size = 15,
